import React, { useState } from 'react';
import './Dashboard.css';
import ChatBot from './ChatBot';

function Dashboard({ userName, onLogout }) {
  const [selectedMood, setSelectedMood] = useState(null);
  const [chatOpen, setChatOpen] = useState(false);

  const moods = [
    { key: 'fantastic', emoji: 'ðŸ˜„', label: 'Fantastic' },
    { key: 'good', emoji: 'ðŸ˜Š', label: 'Good' },
    { key: 'bad', emoji: 'ðŸ˜•', label: 'Bad' },
    { key: 'depressed', emoji: 'ðŸ˜¢', label: 'Depressed' },
  ];

  return (
    <div className="db-container">
      <header className="db-header">
        <h1>MindMate</h1>
        <button className="db-logout" onClick={onLogout}>Logout</button>
      </header>
      <p className="db-welcome">Welcome, <strong>{userName}</strong>!</p>
      <div className="db-question">How are you feeling today?</div>
      <div className="db-moods">
        {moods.map(m => (
          <button
            key={m.key}
            className={`db-mood ${selectedMood === m.key ? 'selected' : ''}`}
            onClick={() => {
              setSelectedMood(m.key);
              setChatOpen(true);
            }}
          >
            <span className="emoji">{m.emoji}</span>
            <span className="label">{m.label}</span>
          </button>
        ))}
      </div>
      {selectedMood && (
        <div className="db-selected">You selected <strong>{selectedMood}</strong></div>
      )}

      {chatOpen && (
        <div className="db-modal-overlay" onClick={() => setChatOpen(false)}>
          <div className="db-modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="db-modal-header">
              <h2>SmartSolv.AI</h2>
              <button className="db-modal-close" onClick={() => setChatOpen(false)}>âœ•</button>
            </div>
            <iframe
              title="SmartSolv AI Chatbot"
              src="https://smartsolv.ai"
              className="db-modal-iframe"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
