import React, { useState } from 'react';
import './Login.css';

function Login({ onLogin }) {
  const [form, setForm] = useState({
    studentName: '',
    age: '',
    email: '',
    password: '',
    qualification: ''
  });

  const qualifications = ['SCHOOLING', 'INTERMEDIATE', 'UNDERGRADUATION', 'POSTGRADUATION'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSelectQualification = (q) => {
    setForm(prev => ({ ...prev, qualification: q }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { studentName, age, email, password, qualification } = form;
    if (!studentName || !age || !email || !password || !qualification) {
      alert('Please fill all fields');
      return;
    }
    // For now just log and optionally call onLogin
    console.log('Login data:', form);
    alert(`Welcome, ${studentName}!`);
    if (onLogin) onLogin(form);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="title">MindMate</h1>
        <form className="login-form" onSubmit={handleSubmit}>
          <label>Student Name</label>
          <input name="studentName" value={form.studentName} onChange={handleChange} placeholder="Enter name" />

          <label>Age</label>
          <input name="age" type="number" value={form.age} onChange={handleChange} placeholder="Enter age" min="1" />

          <label>Email ID</label>
          <input name="email" type="email" value={form.email} onChange={handleChange} placeholder="Enter email" />

          <label>Password</label>
          <input name="password" type="password" value={form.password} onChange={handleChange} placeholder="Enter password" />

          <label>Education Qualification</label>
          <div className="dropdown">
            <button type="button" className="dropdown-toggle">
              {form.qualification || 'Select Qualification'}
            </button>
            <div className="dropdown-menu">
              {qualifications.map((q) => (
                <div key={q} className="dropdown-item" onClick={() => handleSelectQualification(q)}>
                  {q}
                </div>
              ))}
            </div>
          </div>

          <button type="submit" className="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
