import React, { useState } from 'react';
import './Login.css';

function Login({ onLoginSuccess }) {
  const [formData, setFormData] = useState({
    studentName: '',
    age: '',
    email: '',
    password: '',
    qualification: ''
  });

  const [showQualificationDropdown, setShowQualificationDropdown] = useState(false);

  const qualifications = ['SCHOOLING', 'INTERMEDIATE', 'UNDERGRADUATION', 'POSTGRADUATION'];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleQualificationSelect = (qualification) => {
    setFormData(prevState => ({
      ...prevState,
      qualification: qualification
    }));
    setShowQualificationDropdown(false);
  };

  const handleToggleDropdown = () => {
    setShowQualificationDropdown(!showQualificationDropdown);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.studentName || !formData.age || !formData.email || !formData.password || !formData.qualification) {
      alert('Please fill in all fields!');
      return;
    }

    console.log('Form submitted:', formData);
    
    // Call the login success callback with student name
    if (onLoginSuccess) {
      onLoginSuccess(formData.studentName);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1 className="app-title">MindMate</h1>
        <h2 className="login-heading">Student Login</h2>
        
        <form onSubmit={handleSubmit} className="login-form">
          {/* Student Name */}
          <div className="form-group">
            <label htmlFor="studentName">Student Name:</label>
            <input
              type="text"
              id="studentName"
              name="studentName"
              value={formData.studentName}
              onChange={handleInputChange}
              placeholder="Enter your name"
              className="form-input"
            />
          </div>

          {/* Age */}
          <div className="form-group">
            <label htmlFor="age">Age:</label>
            <input
              type="number"
              id="age"
              name="age"
              value={formData.age}
              onChange={handleInputChange}
              placeholder="Enter your age"
              className="form-input"
              min="1"
              max="100"
            />
          </div>

          {/* Email ID */}
          <div className="form-group">
            <label htmlFor="email">Email ID:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              className="form-input"
            />
          </div>

          {/* Password */}
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              className="form-input"
            />
          </div>

          {/* Education Qualification */}
          <div className="form-group">
            <label>Education Qualification:</label>
            <div className="qualification-container">
              <button
                type="button"
                onClick={handleToggleDropdown}
                className="qualification-button"
              >
                {formData.qualification || 'Select Qualification'}
                <span className={`dropdown-arrow ${showQualificationDropdown ? 'open' : ''}`}>
                  â–¼
                </span>
              </button>

              {showQualificationDropdown && (
                <div className="qualification-dropdown">
                  {qualifications.map((qual, index) => (
                    <div
                      key={index}
                      className="qualification-option"
                      onClick={() => handleQualificationSelect(qual)}
                    >
                      {index + 1}. {qual}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Submit Button */}
          <button type="submit" className="submit-button">
            Login
          </button>
        </form>

        {/* Display Selected Qualification */}
        {formData.qualification && (
          <p className="selected-qualification">
            Selected Qualification: <strong>{formData.qualification}</strong>
          </p>
        )}
      </div>
    </div>
  );
}

export default Login;
