import React from 'react';
import './MonthlyRewards.css';

const CHECKLIST_HISTORY_KEY = 'checklistHistory';
const STRESS_HISTORY_KEY = 'stressAnalysisHistory';

const getMonthDates = () => {
  const dates = [];
  const now = new Date();
  const year = now.getFullYear();
  const month = now.getMonth();
  const lastDay = new Date(year, month + 1, 0).getDate();

  for (let day = 1; day <= lastDay; day += 1) {
    const date = new Date(year, month, day);
    dates.push(date.toISOString().split('T')[0]);
  }

  return dates;
};

const computeAverage = (history, dateKeys) => {
  if (!dateKeys.length) {
    return 0;
  }

  const total = dateKeys.reduce((sum, key) => sum + (history[key] ?? 0), 0);
  return Math.round(total / dateKeys.length);
};

const getRewardInfo = (combinedScore) => {
  if (combinedScore >= 85) {
    return {
      grade: 'Gold',
      coins: 120,
      message: 'Outstanding consistency this month. Keep your routine and continue leading by example.'
    };
  }

  if (combinedScore >= 70) {
    return {
      grade: 'Silver',
      coins: 80,
      message: 'Strong monthly progress. With slightly better consistency, you can reach Gold next month.'
    };
  }

  if (combinedScore >= 50) {
    return {
      grade: 'Bronze',
      coins: 45,
      message: 'Good start. Focus on daily completion and stress-care habits to boost your score.'
    };
  }

  return {
    grade: 'Starter',
    coins: 20,
    message: 'Small steps matter. Aim for stable daily habits and your reward level will rise quickly.'
  };
};

function MonthlyRewards({ onClose }) {
  const checklistHistory = JSON.parse(localStorage.getItem(CHECKLIST_HISTORY_KEY) || '{}');
  const stressHistory = JSON.parse(localStorage.getItem(STRESS_HISTORY_KEY) || '{}');

  const monthKeys = getMonthDates();
  const checklistAverage = computeAverage(checklistHistory, monthKeys);
  const stressAverage = computeAverage(stressHistory, monthKeys);
  const combinedScore = Math.round((checklistAverage + stressAverage) / 2);

  const reward = getRewardInfo(combinedScore);

  return (
    <div className="rewards-overlay">
      <div className="rewards-modal">
        <div className="rewards-header">
          <h2>Monthly Reward Report</h2>
          <button className="rewards-close" onClick={onClose}>âœ•</button>
        </div>

        <div className="rewards-body">
          <div className="rewards-score-grid">
            <div className="reward-card">
              <p>Checklist Monthly Average</p>
              <h3>{checklistAverage}%</h3>
            </div>
            <div className="reward-card">
              <p>Stress-Control Monthly Average</p>
              <h3>{stressAverage}%</h3>
            </div>
            <div className="reward-card">
              <p>Combined Monthly Score</p>
              <h3>{combinedScore}%</h3>
            </div>
          </div>

          <div className="coin-section">
            <p className="coin-grade">Reward Tier: <strong>{reward.grade}</strong></p>
            <p className="coin-count">ðŸª™ Earned Coins: <strong>{reward.coins}</strong></p>
            <p className="coin-message">{reward.message}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MonthlyRewards;
